import React from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

Chart.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const DataViz = ({ title, subtitle, result }) => {
  if (!result) {
    return (
      <div className="bg-white shadow-lg rounded-xl p-8">
        <h2 className="text-2xl lg:text-3xl font-bold text-gray-800">{title}</h2>
        <p className="text-gray-600 mt-3 text-lg">{subtitle}</p>
      </div>
    );
  }

  const data = {
    labels: ['Total Investment', 'Total Savings', 'Net Savings'],
    datasets: [
      {
        label: 'Financial Summary',
        data: [result.totalInvestment, result.totalSavings, result.netSavings],
        backgroundColor: [
          'rgba(255, 99, 132, 0.8)',
          'rgba(75, 192, 192, 0.8)',
          'rgba(54, 162, 235, 0.8)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(54, 162, 235, 1)',
        ],
        borderWidth: 1,
        borderRadius: 5,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 14,
            weight: 'bold',
          },
        },
      },
      title: {
        display: true,
        text: 'ROI Financial Overview',
        font: {
          size: 20,
          weight: 'bold',
        },
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            let label = context.dataset.label || '';
            if (label) {
              label += ': ';
            }
            if (context.parsed.y !== null) {
              label += new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(context.parsed.y);
            }
            return label;
          }
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          font: {
            size: 12,
          },
        },
      },
      x: {
        ticks: {
          font: {
            size: 12,
          },
        },
      },
    },
  };

  return (
    <div className="bg-white shadow-2xl rounded-xl p-8">
      <h2 className="text-2xl lg:text-3xl font-bold text-gray-800 mb-2">{title}</h2>
      <p className="text-gray-600 text-lg mb-6">{subtitle}</p>
      <div className="h-96 w-full">
        <Bar data={data} options={options} />
      </div>
    </div>
  );
};

export default DataViz;
