import React, { useEffect, useState } from 'react';
import {
  Document,
  Page,
  Text,
  View,
  Image,
  StyleSheet,
  pdf,
} from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: {
    fontSize: 12,
    padding: 0,
  },
  header: {
    backgroundColor: '#4F46E5',
    padding: 20,
    flexDirection: 'row',
    alignItems: 'center',
  },
  logo: {
    width: 300,
    height: 40,
    marginRight: 12,
  },
  companyName: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#ffffff',
  },
  section: {
    padding: 30,
    paddingTop: 20,
    marginBottom: 15,
  },
  title: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  subtitle: { fontSize: 14, marginBottom: 10, lineHeight: 1.5 },
});

const PdfHeader = ({ company }) => (
  <View style={styles.header}>
    <Image
      src="https://res.cloudinary.com/vantagecircle/image/upload/gatsbycms/uploads/2023/07/new-vc-logo.png"
      style={styles.logo}
    />
  </View>
);

const PdfForm = ({ title, subtitle, result, metrics }) => {
  const [showForm, setShowForm] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [userName, setUserName] = useState('');
  const [companyName, setCompanyName] = useState('');

  useEffect(() => {
    if (showForm && !formSubmitted) {
      const script = document.createElement('script');
      script.src = 'https://js.hsforms.net/forms/embed/v2.js';
      script.async = true;
      script.onload = () => {
        if (window.hbspt) {
          window.hbspt.forms.create({
            region: 'na1',
            portalId: '6153052',
            formId: 'bd19ff99-c7f2-4717-99be-a9b2d490fdff',
            target: '#hubspot-form',
            onFormSubmitted: () => {
              const form = document.querySelector('#hubspot-form form');
              const nameInput = form.querySelector('input[name="firstname"]');
              const companyInput = form.querySelector('input[name="company"]');

              const name = nameInput?.value || '';
              const company = companyInput?.value || '';

              setUserName(name);
              setCompanyName(company);
              setFormSubmitted(true);
              handleDownload(name, company);
            },
          });
        }
      };
      document.body.appendChild(script);
    }
  }, [showForm, formSubmitted]);

  const formatCurrency = (val) =>
    `$${val.toLocaleString(undefined, { maximumFractionDigits: 2 })}`;
  const formatPercent = (val) => `${val?.toFixed(1)}%`;

const ReportPDF = ({ name, company }) => (
  <Document>
    {/* Page 1 */}
    <Page size="A4" style={styles.page}>
      <PdfHeader company={company} />

      <View style={styles.section}>
        <Text style={styles.title}>Employee Recognition ROI Calculator Report</Text>
        {company && <Text>Company: {company}</Text>}
        {name && <Text>Generated for: {name}</Text>}
        <Text>Date: {new Date().toLocaleDateString()}</Text>
        <Text>Industry: {metrics?.industry || 'N/A'}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.subtitle}>EXECUTIVE SUMMARY</Text>
        <Text>
          This report provides a comprehensive analysis of the potential return on investment (ROI) for implementing
          an employee recognition program.
        </Text>
        <Text>
          The analysis shows an excellent ROI of {formatPercent(result.roi)}, indicating a strong business case with
          cost savings and productivity gains.
        </Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.subtitle}>COMPANY PROFILE</Text>
        <Text>Number of Employees: {metrics?.numberOfEmployees}</Text>
        <Text>Average Employee Salary: {formatCurrency(metrics?.averageSalary)}</Text>
        <Text>Revenue per Employee: {formatCurrency(metrics?.revenuePerEmployee)}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.subtitle}>CURRENT SITUATION</Text>
        <Text>Current Turnover Rate: {formatPercent(metrics?.currentTurnoverRate)}</Text>
        <Text>Current Absenteeism Days/Employee: {metrics?.currentAbsenteeismDays}</Text>
        <Text>Daily Cost of Absenteeism: {formatCurrency(metrics?.dailyCostOfAbsenteeism)}</Text>
      </View>
    </Page>

    {/* Page 2 */}
    <Page size="A4" style={styles.page}>
      <PdfHeader company={company} />

      <View style={styles.section}>
        <Text style={styles.subtitle}>PROGRAM INVESTMENT</Text>
        <Text>Annual Subscription Cost: {formatCurrency(result.subscriptionCosts)}</Text>
        <Text>Budget for Rewards (70%): {formatCurrency(result.rewardsBudget)}</Text>
        <Text>Additional Program Costs: {formatCurrency(result.additionalProgramCosts)}</Text>
        <Text>Total Program Cost: {formatCurrency(result.totalInvestment)}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.subtitle}>PROJECTED SAVINGS</Text>
        <Text>Retention Savings: {formatCurrency(result.retentionSavings)}</Text>
        <Text>Productivity Savings: {formatCurrency(result.productivitySavings)}</Text>
        <Text>Absenteeism Savings: {formatCurrency(result.absenteeismSavings)}</Text>
        <Text>Total Annual Savings: {formatCurrency(result.totalSavings)}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.subtitle}>ROI ANALYSIS</Text>
        <Text>Total Investment: {formatCurrency(result.totalInvestment)}</Text>
        <Text>Total Annual Savings: {formatCurrency(result.totalSavings)}</Text>
        <Text>Net Annual Savings: {formatCurrency(result.netSavings)}</Text>
        <Text>Return on Investment: {formatPercent(result.roi)}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.subtitle}>PROJECTED IMPROVEMENTS</Text>
        <Text>
          New Turnover Rate:{' '}
          {(metrics.currentTurnoverRate *
            (1 - metrics.expectedTurnoverReduction / 100)).toFixed(1)}%
        </Text>
        <Text>
          New Absenteeism Days/Employee:{' '}
          {(metrics.currentAbsenteeismDays *
            (1 - metrics.expectedAbsenteeismReduction / 100)).toFixed(1)}
        </Text>
        <Text>Productivity Increase: {formatPercent(metrics.expectedProductivityIncrease)}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.subtitle}>RECOMMENDATIONS</Text>
        <Text>
          The exceptional ROI indicates this program should be implemented immediately. Consider expanding the
          program scope to maximize benefits.
        </Text>
        <Text>
          Key success factors include leadership support, clear communication, and regular program evaluation.
        </Text>
      </View>
    </Page>
  </Document>
);



  const handleDownload = async (name, company) => {
    const blob = await pdf(<ReportPDF name={name} company={company} />).toBlob();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'Employee_Recognition_ROI_Report.pdf';
    a.click();
    URL.revokeObjectURL(url);
    setShowForm(false);
  };

  if (!result) {
    return (
      <div className="w-full mx-auto p-6 bg-white shadow-md rounded-lg space-y-8">
        <h2 className="text-lg lg:text-3xl font-medium">{title}</h2>
        <p className="text-gray-500 mt-2 text-base">{subtitle}</p>
      </div>
    );
  }

  return (
    <div className="w-full mx-auto p-6 bg-white shadow-md rounded-lg space-y-6">
      <h2 className="text-lg lg:text-2xl font-medium text-indigo-100">{title}</h2>
      {subtitle && <p className="text-gray-500 mt-1">{subtitle}</p>}

      {!showForm && (
        <button
          onClick={() => setShowForm(true)}
          className="bg-orange text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
        >
          Download PDF Report
        </button>
      )}

      {showForm && !formSubmitted && (
        <div id="hubspot-form" className="mt-4" />
      )}

      {formSubmitted && (
        <div className="mt-4 text-green-600 font-medium">
          Your report is downloading...
        </div>
      )}
    </div>
  );
};

export default PdfForm;
