import React from 'react';

const FormCard = ({ title, subtitle, fields, dropdownLabel, dropdownOptions, values, onChange }) => {
  return (
    <div className="bg-white shadow-md rounded-md p-6">
      <h2 className="text-lg lg:text-3xl font-medium">{title}</h2>
      <p className="text-gray-500 mt-2 text-base">{subtitle}</p>
      {dropdownLabel && (
        <div>
          <label>{dropdownLabel}</label>
          <select onChange={(e) => onChange('industry', e.target.value)} value={values.industry}>
            <option value="">Select an industry</option>
            {dropdownOptions.map(option => (
              <option key={option.value} value={option.value}>{option.label}</option>
            ))}
          </select>
        </div>
      )}
      {fields && fields.map(field => (
        <div key={field.name}>
          <label>{field.label}</label>
          <input
            type={field.type}
            value={values[field.name]}
            onChange={(e) => onChange(field.name, e.target.value)}
          />
        </div>
      ))}
    </div>
  );
};

export default FormCard;
