import React, { useEffect, useState } from 'react';
import {
  Document,
  Page,
  Text,
  View,
  Image,
  StyleSheet,
  pdf,
} from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: {
    fontSize: 11,
    padding: 40,
    fontFamily: 'Helvetica',
    color: '#333',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  logo: {
    width: 150,
    height: 'auto',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#4F46E5',
    textAlign: 'center',
    marginBottom: 20,
  },
  section: {
    marginBottom: 20,
  },
  sectionTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#4F46E5',
    marginBottom: 10,
    borderBottom: '2px solid #4F46E5',
    paddingBottom: 5,
  },
  subsection: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 5,
  },
  label: {
    fontWeight: 'bold',
  },
  value: {
    textAlign: 'right',
  },
  formula: {
    backgroundColor: '#f0f0f0',
    padding: 10,
    borderRadius: 5,
    marginVertical: 10,
  },
  formulaText: {
    fontFamily: 'Courier',
  },
  calculation: {
    textAlign: 'center',
    fontSize: 14,
    marginVertical: 10,
  },
  result: {
    textAlign: 'center',
    fontSize: 18,
    fontWeight: 'bold',
    color: '#4F46E5',
    marginVertical: 10,
  },
  footer: {
    position: 'absolute',
    bottom: 30,
    left: 40,
    right: 40,
    textAlign: 'center',
    fontSize: 10,
    color: '#888',
  },
});

const PdfHeader = () => (
  <View style={styles.header}>
    <Image
      src="https://res.cloudinary.com/vantagecircle/image/upload/gatsbycms/uploads/2023/07/new-vc-logo.png"
      style={styles.logo}
    />
    <Text>{new Date().toLocaleDateString()}</Text>
  </View>
);

const PdfForm = ({ title, subtitle, result, metrics }) => {
  const [showForm, setShowForm] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [userName, setUserName] = useState('');
  const [companyName, setCompanyName] = useState('');

  useEffect(() => {
    if (showForm && !formSubmitted) {
      const script = document.createElement('script');
      script.src = 'https://js.hsforms.net/forms/embed/v2.js';
      script.async = true;
      script.onload = () => {
        if (window.hbspt) {
          window.hbspt.forms.create({
            region: 'na1',
            portalId: '6153052',
            formId: 'bd19ff99-c7f2-4717-99be-a9b2d490fdff',
            target: '#hubspot-form',
            onFormSubmitted: () => {
              const form = document.querySelector('#hubspot-form form');
              const nameInput = form.querySelector('input[name="firstname"]');
              const companyInput = form.querySelector('input[name="company"]');

              const name = nameInput?.value || '';
              const company = companyInput?.value || '';

              setUserName(name);
              setCompanyName(company);
              setFormSubmitted(true);
              handleDownload(name, company);
            },
          });
        }
      };
      document.body.appendChild(script);
    }
  }, [showForm, formSubmitted]);

  const formatCurrency = (val) =>
    `$${val.toLocaleString(undefined, { maximumFractionDigits: 2 })}`;

  const ReportPDF = ({ name, company }) => (
    <Document>
      <Page size="A4" style={styles.page}>
        <PdfHeader />
        <Text style={styles.title}>Employee Recognition ROI Report</Text>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Your Answers</Text>
          <View style={styles.subsection}>
            <Text style={styles.label}>Employees</Text>
            <Text style={styles.value}>{metrics.numberOfEmployees}</Text>
          </View>
          <View style={styles.subsection}>
            <Text style={styles.label}>Avg. Annual Salary</Text>
            <Text style={styles.value}>{formatCurrency(metrics.averageSalary)}</Text>
          </View>
          <View style={styles.subsection}>
            <Text style={styles.label}>Turnover Rate</Text>
            <Text style={styles.value}>{metrics.currentTurnoverRate}%</Text>
          </View>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Investment</Text>
          <View style={styles.formula}>
            <Text style={styles.formulaText}>Employee count × Avg. salary × 1% of total compensation</Text>
          </View>
          <Text style={styles.calculation}>
            {metrics.numberOfEmployees} x {formatCurrency(metrics.averageSalary)} x 0.01
          </Text>
          <Text style={styles.result}>= {formatCurrency(result.totalInvestment)}</Text>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Subscription</Text>
          <View style={styles.formula}>
            <Text style={styles.formulaText}>Employee count x (Subscription fee x 12 months) x Avg. % of company given budget</Text>
          </View>
          <Text style={styles.calculation}>
            {metrics.numberOfEmployees} x ${metrics.monthlySubscriptionFee} x 12 x 0.20 = {formatCurrency(result.subscriptionCosts)}
          </Text>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Rewards</Text>
          <View style={styles.formula}>
            <Text style={styles.formulaText}>Recommended investment - total subscription fees</Text>
          </View>
          <Text style={styles.calculation}>
            {formatCurrency(result.totalInvestment)} - {formatCurrency(result.subscriptionCosts)} = {formatCurrency(result.rewardsBudget)}
          </Text>
        </View>

        <Text style={styles.footer}>
          This is an auto-generated report. The values are based on the data you provided.
        </Text>
      </Page>
    </Document>
  );

  const handleDownload = async (name, company) => {
    const blob = await pdf(<ReportPDF name={name} company={company} />).toBlob();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'Employee_Recognition_ROI_Report.pdf';
    a.click();
    URL.revokeObjectURL(url);
    setShowForm(false);
  };

  if (!result) {
    return (
      <div className="w-full mx-auto p-6 bg-white shadow-md rounded-lg space-y-8">
        <h2 className="text-lg lg:text-3xl font-medium">{title}</h2>
        <p className="text-gray-500 mt-2 text-base">{subtitle}</p>
      </div>
    );
  }

  return (
    <div className="w-full mx-auto p-6 bg-white shadow-md rounded-lg space-y-6">
      <h2 className="text-lg lg:text-2xl font-medium text-indigo-100">{title}</h2>
      {subtitle && <p className="text-gray-500 mt-1">{subtitle}</p>}

      {!showForm && (
        <button
          onClick={() => setShowForm(true)}
          className="bg-orange text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
        >
          Download PDF Report
        </button>
      )}

      {showForm && !formSubmitted && (
        <div id="hubspot-form" className="mt-4" />
      )}

      {formSubmitted && (
        <div className="mt-4 text-green-600 font-medium">
          Your report is downloading...
        </div>
      )}
    </div>
  );
};

export default PdfForm;
