import React from 'react';

const DataViz = ({ title, subtitle, result }) => {
  if (!result) {
    return (
      <div className="bg-white shadow-md rounded-md p-6">
        <h2 className="text-lg lg:text-3xl font-medium">{title}</h2>
        <p className="text-gray-500 mt-2 text-base">{subtitle}</p>
      </div>
    );
  }

  return (
    <div className="bg-white shadow-md rounded-md p-6">
      <h2 className="text-lg lg:text-2xl font-medium text-indigo-100">{title}</h2>
      <p className="text-gray-500 text-base mt-3">{subtitle}</p>
      <div>
        <h3>ROI: {result.roi.toFixed(1)}%</h3>
        <p>Total Savings: ${result.totalSavings.toLocaleString()}</p>
        <p>Total Investment: ${result.totalInvestment.toLocaleString()}</p>
      </div>
    </div>
  );
};

export default DataViz;
